import React, { Component, Fragment } from "react";
import { AUTH_TOKEN } from "../utils/constants";
import { Mutation } from "react-apollo";
import gql from "graphql-tag";
import { UserConsumer } from './App'
//STYLING
import { withStyles } from "@material-ui/core/styles";
import {
  Container,
  Avatar,
  Card,
  Button,
  TextField,
  Link,
  Grid,
  Typography
} from "@material-ui/core";

const SIGNUP_MUTATION = gql`
  mutation SignupMutation($email: String!, $password: String!, $name: String!) {
    signup(email: $email, password: $password, name: $name) {
      token,
      user{
        id
        name
        email
      }
    }
  }
`;

const LOGIN_MUTATION = gql`
  mutation LoginMutation($email: String!, $password: String!) {
    login(email: $email, password: $password) {
      token,
      user{
        id
        name
        email
      }
    }
  }
`;

const styles = theme => ({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
  },
  paper: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  card: {
    padding: theme.spacing(2)
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1)
  },
  submit: {
    margin: theme.spacing(3, 0, 2)
  }
});

class Auth extends Component {
  constructor(props) {
    super(props);
    this.state = {
      login: true,
      email: "",
      password: "",
      name: ""
    };
  }

  saveUserData = token => {
    localStorage.setItem(AUTH_TOKEN, token);
  };

  confirm = async (data) => {
    console.log('CONFIRM')
    const res = this.state.login ? data.login : data.signup;
    console.log(this.context)
    // console.log(res)
    // this.props.updateUser(res)
    // this.props.history.push(`/`);
  };

  handleChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  render(){
    const {login, name, email, password} = this.state
    const { classes, token } = this.props;
    return (
      <UserConsumer>
        {(context) => {
          return (      
            <Container component="main" className={classes.root} maxWidth="xs">
            <Card className={classes.card}>
              <div className={classes.paper}>
                <Avatar className={classes.avatar}></Avatar>
                <Typography component="h1" variant="h5">
                  {login ? "Sign in" : "Sign Up"}
                </Typography>
                <form className={classes.form} noValidate>
                  <Grid container spacing={2}>
                    <Grid item xs={12}>
                      {!login && (
                        <TextField
                          onChange={e => {
                            this.handleChange(e);
                          }}
                          autoComplete="name"
                          name="Name"
                          variant="outlined"
                          required
                          fullWidth
                          id="Name"
                          label="Name"
                          autoFocus
                        />
                      )}
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        onChange={e => {
                          this.handleChange(e);
                        }}
                        variant="outlined"
                        required
                        fullWidth
                        id="email"
                        label="Email Address"
                        name="email"
                        autoComplete="email"
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        onChange={e => {
                          this.handleChange(e);
                        }}
                        variant="outlined"
                        required
                        fullWidth
                        name="password"
                        label="Password"
                        type="password"
                        id="password"
                        autoComplete="current-password"
                      />
                    </Grid>
                  </Grid>

                  <Mutation
                    mutation={login ? LOGIN_MUTATION : SIGNUP_MUTATION}
                    variables={{ email, password, name }}
                    onCompleted={(data)=> {this.confirm(data, context)}}
                    onError={(e) => {console.error(e)}}
                >
                  {(mutation, { loading, error, data, called }) => (
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    color="primary"
                    className={classes.submit}
                    onClick={(e) => {
                      e.preventDefault()
                      mutation()
                    }}
                  >
                    {login ? "Sign in" : "Sign Up"}
                  </Button>
                  )}
                  </Mutation>
                  {login ? (
                    <Grid container>
                      <Grid item xs>
                        <Link href="#" variant="body2">
                          Forgot password?
                        </Link>
                      </Grid>
                      <Grid item>
                        <Link
                          href="#"
                          onClick={() => {

                            this.setState({ login: false });
                          }}
                          variant="body2"
                        >
                          {"Don't have an account? Sign Up"}
                        </Link>
                      </Grid>
                    </Grid>
                  ) : (
                    <Grid container justify="flex-end">
                      <Grid item>
                        <Link
                          href="#"
                          onClick={() => {
                            this.setState({ login: true });
                          }}
                          variant="body2"
                        >
                          Already have an account? Sign in
                        </Link>
                      </Grid>
                    </Grid>
                  )}
                </form>
              </div>
            </Card>
          </Container>
        )
      }}
      </UserConsumer> 
  );
}
}

export default withStyles(styles)(Auth);
