import React, { Component } from 'react'
import { AUTH_TOKEN } from '../utils/constants'
import { Mutation } from 'react-apollo'
import gql from 'graphql-tag'
//STYLING
import { withStyles } from '@material-ui/core/styles';
import { Container, Card, CardContent, InputLabel, FormGroup, Input, FormLabel, FormControl, FormControlLabel, FormHelperText } from '@material-ui/core';

const SIGNUP_MUTATION = gql`
  mutation SignupMutation($email: String!, $password: String!, $name: String!) {
    signup(email: $email, password: $password, name: $name) {
      token
    }
  }
`

const LOGIN_MUTATION = gql`
  mutation LoginMutation($email: String!, $password: String!) {
    login(email: $email, password: $password) {
      token
    }
  }
`

const styles = theme => ({
  root: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  card:{
    width: '250px',
    backgroundColor: 'red'
  }
});

class Auth extends Component {
  state = {
    login: true, // switch between Login and SignUp
    email: '',
    password: '',
    name: '',
  }

  _confirm = async data => {
    const { token } = this.state.login ? data.login : data.signup
    this._saveUserData(token)
    this.props.history.push(`/`)
  }

  render() {
    const { login, email, password, name } = this.state
    const { classes } = this.props;
    console.log(classes)
    return (
      <Container className={classes.root}>
        <Card className={classes.card}>
          <CardContent >   
            <FormControl>
              <InputLabel htmlFor="email-input"> Email address </InputLabel>
              <Input id="email-input" aria-describedby="email-helper-text" onChange={e => this.setState({ email: e.target.value })} />
              <FormHelperText id="email-helper-text"> We'll never share your email. </FormHelperText>
            </FormControl>
            </CardContent> 
        </Card>
      </Container>
    )
  }

  _confirm = async () => {
    // ... you'll implement this ðŸ”œ
  }

  _saveUserData = token => {
    localStorage.setItem(AUTH_TOKEN, token)
  }
}

export default withStyles(styles)(Auth)
