import React, {Component} from 'react';
import Layout from './Layout';
import { Switch, Route } from 'react-router-dom'
import Auth from './Auth'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      token: null,
      user: {}
    }
  }

  updateUser(data){
    console.log(data)
    this.setState({token: data.token, user: data.user})
  }


  render(){
  return (
    <Layout>
      <Switch>
        <Route exact path="/auth" render={(props) => <Auth {...props} user={this.state.user} updateUser={(data) => {this.updateUser(data)}} />}  />
      </Switch>
    </Layout>
  );
}
}

export default App;
