import React, { Component } from 'react'
import { AUTH_TOKEN } from '../utils/constants'
import { useMutation } from 'react-apollo'
import gql from 'graphql-tag'
//STYLING
import { withStyles } from '@material-ui/core/styles';
import { Container, Avatar, Card, Button, TextField, FormControlLabel, Checkbox, Link, Grid, Typography } from '@material-ui/core';

const SIGNUP_MUTATION = gql`
  mutation SignupMutation($email: String!, $password: String!, $name: String!) {
    signup(email: $email, password: $password, name: $name) {
      token
    }
  }
`

const LOGIN_MUTATION = gql`
  mutation LoginMutation($email: String!, $password: String!) {
    login(email: $email, password: $password) {
      token
    }
  }
`

const styles = theme => ({
  root:{
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  paper: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  card:{
    padding: 10
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
});

class Auth extends Component {
  state = {
    login: true, // switch between Login and SignUp
    email: '',
    password: '',
    name: '',
  }

  _confirm = async data => {
    const { token } = this.state.login ? data.login : data.signup
    this._saveUserData(token)
    this.props.history.push(`/`)
  }

  handleChange(e){
    console.log(e.target.name)
    console.log(e.target.value)
  }


  signIn = (classes) => {
    const [signInGQL] = useMutation(LOGIN_MUTATION);
    return(
      <div className={classes.paper}>
      <Avatar className={classes.avatar}>

      </Avatar>
      <Typography component="h1" variant="h5">
        Sign in
      </Typography>
      <form className={classes.form} noValidate>
        <TextField
          onChange={(e)=>{this.handleChange(e)}}
          variant="outlined"
          margin="normal"
          required
          fullWidth
          id="email"
          label="Email Address"
          name="email"
          autoComplete="email"
          autoFocus
        />
        <TextField
          onChange={(e)=>{this.handleChange(e)}}
          variant="outlined"
          margin="normal"
          required
          fullWidth
          name="password"
          label="Password"
          type="password"
          id="password"
          autoComplete="current-password"
        />
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          className={classes.submit}
          onClick={()=> {signInGQL({variables: {email, password}})}}
        >
          Sign In
        </Button>
        <Grid container>
          <Grid item xs>
            <Link href="#" variant="body2">
              Forgot password?
            </Link>
          </Grid>
          <Grid item>
            <Link href="#" onClick={() => {this.setState({login: false})}} variant="body2">
              {"Don't have an account? Sign Up"}
            </Link>
          </Grid>
        </Grid>
      </form>
    </div>
    )
  }

  signUp(classes){
    const [signUpGQL] = useMutation(SIGNUP_MUTATION);
    return(
      <div className={classes.paper}>
      <Avatar className={classes.avatar}>

      </Avatar>
      <Typography component="h1" variant="h5">
        Sign up
      </Typography>
      <form className={classes.form} noValidate>
        <Grid container spacing={2}>
          <Grid item xs={12} >
            <TextField
              onChange={(e)=>{this.handleChange(e)}}
              autoComplete="name"
              name="Name"
              variant="outlined"
              required
              fullWidth
              id="Name"
              label="Name"
              autoFocus
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              onChange={(e)=>{this.handleChange(e)}}
              variant="outlined"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              onChange={(e)=>{this.handleChange(e)}}
              variant="outlined"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
          </Grid>
        </Grid>
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          className={classes.submit}
          onClick={()=> {signUpGQL({variables: {email, password, name}})}}
        >
          Sign Up
        </Button>
        <Grid container justify="flex-end">
          <Grid item>
            <Link href="#" onClick={() => {this.setState({login: true})}} variant="body2">
              Already have an account? Sign in
            </Link>
          </Grid>
        </Grid>
      </form>
    </div>
    )
  }

  render() {
    const { login, email, password, name } = this.state
    const { classes } = this.props;
    return (
    <Container component="main" className={classes.root} maxWidth="xs">
      <Card className={classes.card}>
        {login && this.signIn(classes)}
        {!login && this.signUp(classes)}
        </Card>
    </Container>
    )
  }

  _confirm = async () => {
    // ... you'll implement this ðŸ”œ
  }

  _saveUserData = token => {
    localStorage.setItem(AUTH_TOKEN, token)
  }
}

export default withStyles(styles)(Auth)
